# Generate random numberimport randomdef RandomGenGaussianValue(x, mean, st_dev):  expo =  math.pow((x - mean) / st_dev, 2.0)  f_den = (st_dev*math.sqrt(2.0 * math.pi))  ret_val = (1.0/f_den) * math.exp(-(1.0/2.0) * expo)    return ret_valdef RandomGenAchiloptasValue(x):  #elif x <= 1:  ret_val = 1    if x < 1/6:    ret_val = -1  elif x < 5/6:    ret_val = 0    return ret_val