# Read vocabularydef ReadVocabulary(VOCAB_FILE):  word_list = {}  index = 1  with open(VOCAB_FILE,'r+') as file_reader:    for line in file_reader:      word = line.replace('\n','')      # Update List to maintain order      word_list[index] = word      index = index + 1	    file_reader.close()  return word_listdef ReadDocuments(DOCWORD_FILE, max_documents):  doc_reader = open(DOCWORD_FILE, 'r+')  D = int(doc_reader.readline().replace('\n',''))  W = int(doc_reader.readline().replace('\n',''))  NNZ = int(doc_reader.readline().replace('\n',''))    documents = dict()    for x in range(1, max_documents + 1):    documents[x] = dict()    for x in range(NNZ):    wordList = doc_reader.readline().replace('\n', '').split()    doc_id = int(wordList[0])      if doc_id > max_documents:      break      word_id = int(wordList[1])    count = int(wordList[2])      documents[doc_id][word_id] = count      doc_reader.close()      return documents, D, W, NNZ,   # print a bag of words docdef print_bag_doc(document, vocab):  for name, count in document.items():    print('{0:25} --> {1:10d}'.format(vocab[name], count))#print_bag_doc(documents[1], word_list